            # capability is provided by Compute Node Type\n            node: my_block_storage\n            capability: tosca.capabilities.Attachment\n            relationship:\n              type: tosca.relationships.AttachesTo\n              properties:\n                #location: /export\n                device: hdb\n\n    my_block_storage:\n      type: tosca.nodes.BlockStorage\n      properties:\n        size: 1 GB\n\n  outputs:\n    endpoint:\n      value: { get_attribute: [ simple_node, public_address, 0 ] }\n    node_creds:\n      value: { get_attribute: [ simple_node, endpoint, credential, 0 ] }\n','{\"additional_description\": \"\"}','{\"keepLastAttempt\": \"false\"}',0,1,0,'https://iam.recas.ba.infn.it/',0,NULL,NULL,NULL),('11e99fc8-63a3-6928-a6d8-024283ff312a','2019-07-06 08:31:00','2019-07-06 09:25:12','6ff7b594-9fc8-11e9-b39a-fa163e0663d5','','DELETE_COMPLETE','{\"node_creds\": {\"token_type\": \"private_key\", \"user\": \"cloudadm\", \"token\": \"-----BEGIN RSA PRIVATE KEY-----\nMIIEpwIBAAKCAQEApcEtywuR2+cb3Rhriib3Yr08KYJhEmQh9tBmhvzJiBb+jDb2\n7cg1YwryB3Sx+CIraRhp+XJmkjJFsCGyADAM0xwMcgEzx9j+RDe/IlLZroY4ENqo\n8uHAhb8D8ZGaJKHe3DL81GYnCoIRVq8/7UFji751aD9FV06MzJnXbSKgSDbnyG0l\nBcNIfdcUvE3u3VX/P2wR/LzDjrAOxgGClPGS5yvvrX0Y9Zq9J4jjw8Ektqn0hTKx\ndyijwxNNjNTMvVkfKKcPuIdcb8PSLPDSCStRhTz+C/2m/oRxs4ad69YTXygerIL1\nQuH8381cFFN5JHESuHuda/e6AUwvHNCvt/3W7wIDAQABAoIBACqln4Ckg2zO865Z\nQCIVkIUJJ1eOwicXOF3mlNqaY8fuEqRE6BeZ8ZKAGHGuprOvJvgtXebedmGbKa79\nnGzL8CDJELKseIQs4o7uwPC13Gg5BGfhuqJzTzOaSAt5aO2P8GU7gS2s8tKTUJ67\nDHmg31nvMQoXbWHfRf7GflUIGbE6dQfg8WrQGoHxxQDTvoAcmbF8HYUTWm/ccMLz\nQMi34qZM8JmO8kweMV99MdEn7QKXAaz8VJo3nFTlfMwMC0ZdtFROGPTaq505bErW\nV+pSr9qDjSNnxKvhHxoyZUsCd5EV2coiVsklbpmE8KkuaaCrwALn7iosJVEBkLTn\nPgeh8FECggCBAMGb/YX3GoERf/CESEtztW0heyHCbjlnNnd+U5MmA1sJPeoIu1+M\nSf91GasrwAtN2U3kF4OH5h2Va9DHU0h7mlsnMZ7I9uXkO3aeLyzL1nuB+GOLi+8n\n/B3A5V3FMbx8KoNP1r+IqxaUa1EFu1NcPt90BC3LvmLQ2gPfXxHBfxmdAoIAgQDb\nK0hXP/KS+86E1BUS0h+1Vor1238o+HU9SjrUMncjw1ZHX9ZIcM7SE3/+G2yV9Izv\nUjh