adata:\n      type: string\n      description: Host metadata to be sent to the Zabbix server\n      required: no\n      default: \"\"\n  node_templates:\n    simple_node:\n      type: tosca.nodes.indigo.Compute\n      properties:\n        zabbix_server: { get_input: zabbix_server }\n        zabbix_server_metadata: { get_input: zabbix_metadata }\n      capabilities:\n        endpoint:\n          properties:\n            network_name: PUBLIC\n        scalable:\n          properties:\n            count: { get_input: num_instances }\n        host:\n          properties:\n            #instance_type: m1.small\n            num_cpus: { get_input: num_cpus }\n            mem_size: { get_input: mem_size }\n        os:\n          properties:\n            image: ubuntu-16.04-vmi\n            #distribution: { get_input: os_distribution }\n            #type: linux\n  outputs:\n    endpoint:\n      value: { get_attribute: [ simple_node, public_address, 0 ] }\n    node_creds:\n      value: { get_attribute: [ simple_node, endpoint, credential, 0 ] }\n','{\"zabbix_server\": \"\", \"zabbix_metadata\": \"\", \"os_distribution\": \"ubuntu\", \"num_cpus\": \"2\", \"num_instances\": \"1\", \"additional_description\": \"\", \"mem_size\": \"3 GB\"}','{\"keepLastAttempt\": \"false\"}',0,1,1,'https://iam.recas.ba.infn.it/',0,'','',''),('11e9a2fe-1d6d-f8eb-a6d8-024283ff312a','2019-07-10 10:33:00','2019-07-10 10:53:00','2922ad52-a2fe-11e9-b04e-fa163e0663d5','rna-workbench','DELETE_COMPLETE','{}','NONE','[{\"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9a2fe-1d6d-f8eb-a6d8-024283ff312a\", \"rel\": \"self\"}, {\"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9a2fe-1d6d-f8eb-a6d8-024283ff312a/resources\", \"rel\": \"resources\"}, {\"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9a2fe-1d6d-f8eb-a6d8-024283ff312a/template\", \"rel\": \"template\"}]','20340b0b-c07f-46e0-a9c6-1f987ba9ff85','provider-RECAS-BARI','','tosca_definitions_version: tosca_simple_yaml_1_0\n\nimports:\n  - indigo_custom_types: https://raw.githubusercontent.com/mtangaro/tosca-types/master/custom_types.yaml\n\ndescription: >\n  Deploy Galaxy on a single Virtual Machine installing it from scratch (SLOW).\n  The basic configuration includes CentOS 7, the selected Galaxy flavour, companion software and reference data.\n  Configure, click on the \"Submit\" button and wait for the confirmation e-mail(s) with instructions on how to provide your passphrase (if encryption is enabled) and log in to your new Galaxy instance.\n  If after some hours you do not receive any e-mail please be sure to check your SPAM BOX.\n\ntopology_template:\n  inputs:\n    instance_flavor:\n      type: string\n      description: instance flavor (num_cpu, memory, disk)\n      default: small\n    storage_size:\n      type: string\n      description: storage memory required for the instance\n      default: 10 GB      \n\n    admin_email:\n      type: string\n      description: email of the admin user\n      default: admin@admin.com\n    admin_api_key:\n      type: string\n      description: key to access the API with admin role\n      default: not_very_secret_api_key\n    user:\n      type: string\n      description: username to launch the galaxy daemon\n      default: galaxy\n    version:\n      type: string\n      description: galaxy version to install\n      default: master\n    instance_description:\n      type: string\n      description: galaxy instance description\n      default: \"INDIGO Galaxy test\"\n    instance_key_pub:\n      type: string\n      description: galaxy instance ssh public key\n      default: your_ssh_public_key\n    export_dir:\n      type: string\n      description: path to store galaxy data\n      default: /export\n\n    storage_encryption:\n      type: boolean\n      description: Enable storage encryption using Vault to store secrets and LUKS to encrypt\n      default: False\n      required: true\n    vault_url:\n      type: string\n      description: Hashicorp Vault server url\n      default: \"https://cloud-90-147-75-223.cloud.ba.infn.it:8200\"\n      required: false\n    vault_wrapping_token:\n      type: string\n      description: Vault Wrapping token to write secret\n      default: not_a_wrapping_token\n      required: false\n    vault_secret_path:\n      type: string\n      description: Vault path to store secret\n      default: \"subject/depuuid\"\n      required: false\n    vault_secret_key:\n      type: string\n      description: Vault secret key name\n      default: secret\n      required: false\n    iam_client_id:\n      type: string\n      description:\n      default: iam_client_id_for_luks_client\n      required: false\n    iam_client_secret:\n      type: string\n      description:\n      default: iam_secret_for_luks_client\n      required: false\n\n    flavor:\n      type: string\n      description: Galaxy flavor for tools installation\n      default: \"galaxy-no-tools\"\n\n    reference_data:\n      type: boolean\n      description: Install Reference data\n      default: true\n    refdata_dir:\n      type: string\n      description: path to store galaxy reference data\n      default: /cvmfs\n    refdata_repository_name:\n      type: string\n      description: Onedata space name, CernVM-FS repository name or subdirectory downaload name\n      default: \'elixir-italy.galaxy.refdata\'\n    refdata_provider_type:\n      type: string\n      description: Select Reference data provider type (Onedata, CernVM-FS or download)\n      default: \'cvmfs_preconfigured\'\n    refdata_provider:\n      type: string\n      description: Oneprovider for reference data\n      default: \'not_a_provider\'\n    refdata_token:\n      type: string\n      description: Access token for reference data\n      default: \'not_a_token\'\n    refdata_cvmfs_server_url:\n      type: string\n      description: CernVM-FS server, replica or stratum-zero\n      default: \'90.147.102.186\'\n    refdat