INSERT INTO `deployments` VALUES ('11e9ac66-c6ab-f2ce-bc29-024283ff312a','2019-07-22 09:55:00','2019-07-22 11:18:14','d62ffa10-ac66-11e9-8af8-fa163e0663d5','docker_stable','DELETE_COMPLETE','{\"endpoint\": \"http://90.147.170.136\", \"node_ip\": \"90.147.170.136\"}','NONE','[{\"rel\": \"self\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac66-c6ab-f2ce-bc29-024283ff312a\"}, {\"rel\": \"resources\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac66-c6ab-f2ce-bc29-024283ff312a/resources\"}, {\"rel\": \"template\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac66-c6ab-f2ce-bc29-024283ff312a/template\"}]','c0b907df-43a3-4c8a-952a-2b5ca56ec43e','provider-RECAS-BARI','http://90.147.170.136','tosca_definitions_version: tosca_simple_yaml_1_0\n\nimports:\n  - indigo_custom_types: https://raw.githubusercontent.com/mtangaro/tosca-types/master/custom_types.yaml\n\ndescription: >\n  TOSCA test for launching a Galaxy Server also configuring the bowtie2\n  tool using Galaxy Tool Shed.\n\ntopology_template:\n  inputs:\n    instance_flavor:\n      type: string\n      description: instance flavor (num_cpu, memory, disk)\n      default: small\n    storage_size:\n      type: string\n      description: storage memory required for the instance\n      default: 50 GB      \n\n    admin_email:\n      type: string\n      description: email of the admin user\n      default: ma.tangaro@gmail.com\n    admin_api_key:\n      type: string\n      description: key to access the API with admin role\n      default: GALAXY_ADMIN_API_KEY\n    user:\n      type: string\n      description: username to launch the galaxy daemon\n      default: galaxy\n    version:\n      type: string\n      description: galaxy version to install\n      default: release_18.05\n    instance_description:\n      type: string\n      description: galaxy instance description\n      default: \"INDIGO Galaxy test\"\n    instance_key_pub:\n      type: string\n      description: galaxy instance ssh public key\n      default: \'ssh-rsa  marco@MacBook-Pro-di-Marco.local\'\n    export_dir:\n      type: string\n      description: path to store galaxy data\n      default: /export\n\n    storage_encryption:\n      type: boolean\n      description: Enable storage encryption using Vault to store secrets and LUKS to encrypt\n      default: False\n      required: true\n    vault_url:\n      type: string\n      description: Hashicorp Vault server url\n      default: \"https://cloud-90-147-75-223.cloud.ba.infn.it:8200\"\n      required: false\n    vault_wrapping_token:\n      type: string\n      description: Vault Wrapping token to write secret\n      default: not_a_wrapping_token\n      required: false\n    vault_secret_path:\n      type: string\n      description: Vault path to store secret\n      default: \"subject/depuuid\"\n      required: false\n    vault_secret_key:\n      type: string\n      description: Vault secret key name\n      default: secret\n      required: false\n\n    flavor:\n      type: string\n      description: Galaxy flavor for tools installation\n      default: \"bgruening/galaxy-stable\"\n\n    reference_data:\n      type: boolean\n      description: Install Reference data\n      default: true\n\n \n  node_templates:\n\n    galaxy:\n      type: tosca.nodes.indigo.GalaxyPortalDocker\n      properties:\n        storage_encryption: { get_input: storage_encryption }\n        vault_url: { get_input: vault_url }\n        vault_wrapping_token: { get_input: vault_wrapping_token }\n        vault_secret_path: { get_input: vault_secret_path }\n        vault_secret_key: { get_input: vault_secret_key }\n        admin_email: { get_input: admin_email }\n        admin_api_key: { get_input: admin_api_key }\n        version: { get_input: version }\n        instance_description: { get_input: instance_description }\n        instance_key_pub: { get_input: instance_key_pub }\n        export_dir: { get_input: export_dir }\n        flavor: { get_input: flavor }\n      requirements:\n        - lrms: local_lrms\n\n    # type to describe a Galaxy not using any LRMS but using the local system\n    local_lrms:\n      type: tosca.nodes.indigo.LRMS.FrontEnd.Local\n      requirements:\n        - host: galaxy_server\n \n    galaxy_server:\n      type: tosca.nodes.indigo.Compute\n      capabilities:\n        endpoint:\n          properties:\n            network_name: PUBLIC\n            #private_ip: false\n            ports:\n              http_port:\n                protocol: tcp\n                source: 80\n              ftp_port:\n                protocol: tcp\n                source: 21\n              ftp_passive:\n                protocol: tcp\n                source_range: [ 30000, 30009 ]\n        # Host container properties\n        host:\n         properties:\n           instance_type: { get_input: instance_flavor }\n        # Guest Operating System properties\n        os:\n          properties:\n            image: centos-7-galaxy-base \n      requirements:\n        # contextually this can only be a relationship type\n        - local_storage:\n            # capability is provided by Compute Node Type\n            node: my_block_storage\n            capability: tosca.capabilities.Attachment\n            relationship:\n              type: tosca.relationships.AttachesTo\n              properties:\n                location: { get_input: export_dir }\n                device: hdb\n\n    my_block_storage:\n      type: tosca.nodes.BlockStorage\n      properties:\n        size: { get_input: storage_size }\n\n  outputs:\n    endpoint:\n      value: { concat: [ \'http://\', get_attribute: [ galaxy_server, public_address, 0 ] ] }\n    node_ip:\n      value: { get_attribute: [ galaxy_server, public_address, 0 ] }\n','{\"instance_description\": \"ELIXIR-ITALY\", \"flavor\": \"bgruening/galaxy-stable\", \"additional_description\": \"docker_stable\", \"admin_email\": \"pietro.mandreoli@gmail.com\", \"storage_size\": \"50 GB\", \"vault_secret_key\": \"secret\", \"refdata_cvmfs_repository_name\": \"elixir-italy.covacs.refdata\", \"version\": \"release_18.05\", \"instance_key_pub\": \"ssh-rsa  marco@MacBook-Pro-di-Marco.local\", \"instance_flavor\": \"small\"}','{\"keepLastAttempt\": \"false\"}',0,1,0,'https://iam.recas.ba.infn.it/',0,'','',''),('11e9ac6d-201d-13ec-bc29-024283ff312a','2019-07-22 10:40:00','2019-07-22 11:29:20','2a2f5f06-ac6d-11e9-b39a-fa163e0663d5','api5','DELETE_COMPLETE','{\"node_io\": \"http://90.147.170.170\", \"endpoint\": \"http://90.147.170.170/galaxy\"}','NONE','[{\"rel\": \"self\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac6d-201d-13ec-bc29-024283ff312a\"}, {\"rel\": \"resources\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac6d-201d-13ec-bc29-024283ff312a/resources\"}, {\"rel\": \"template\", \"href\": \"http://cloud-90-147-75-155.cloud.ba.infn.it:8080/deployments/11e9ac6d-201d-13ec-bc29-024283ff312a/template\"}]','20340b0b-c07f-46e0-a9c6-1f987ba9ff85','provider-RECAS-BARI','http://90.147.170.170/galaxy','tosca_definitions_version: tosca_simple_yaml_1_0\n\nimports:\n  - indigo_custom_types: https://raw.githubusercontent.com/mtangaro/tosca-types/master/custom_types.yaml\n\ndescription: >\n  Deploy Galaxy on a single Virtual Machine installing it from scratch (SLOW).\n  The basic configuration includes CentOS 7, the selected Galaxy flavour, companion software and reference data.\n  Configure, click on the \"Submit\" button and wait for the confirmation e-mail(s) with instructions on how to provide your passphrase (if encryption is enabled) and log in to your new Galaxy instance.\n  If after some hours you do not receive any e-mail please be sure to check your SPAM BOX.\n\ntopology_template:\n  inputs:\n    instance_flavor:\n      type: string\n      description: instance flavor (num_cpu, memory, disk)\n      default: small\n    storage_size:\n      type: string\n      description: storage memory required for the instance\n      default: 10 GB      \n\n    admin_email:\n      type: string\n      description: email of the admin user\n      default: admin@admin.com\n    admin_api_key:\n      type: string\n      description: key to access the API with admin role\n      default: not_very_secret_api_key\n    user:\n      type: string\n      description: username to launch the galaxy daemon\n      default: galaxy\n    version:\n      type: string\n      description: galaxy version to install\n      default: master\n    instance_description:\n      type: string\n      description: galaxy instance description\n      default: \"INDIGO Galaxy test\"\n    instance_key_pub:\n      type: string\n      description: galaxy instance ssh public key\n      default: your_ssh_public_key\n    export_dir:\n      type: string\n      description: path to store galaxy data\n      default: /export\n\n    storage_encryption:\n      type: boolean\n      description: Enable storage encryption using Vault to store secrets and LUKS to encrypt\n      default: False\n      required: true\n    vault_url:\n      type: string\n      description: Hashicorp Vault server url\n      default: \"https://cloud-90-147-75-223.cloud.ba.infn.it:8200\"\n      required: false\n    vault_wrapping_token:\n      type: string\n      description: Vault Wrapping token to write secret\n      default: not_a_wrapping_token\n      required: false\n    vault_secret_path:\n      type: string\n      description: Vault path to store secret\n      default: \"subject/depuuid\"\n      required: false\n    vault_secret_key:\n      type: string\n      description: Vault secret key name\n      default: secret\n      required: false\n\n    flavor:\n      type: string\n      description: Galaxy flavor for tools installation\n      default: \"galaxy-no-tools\"\n\n    reference_data:\n      type: boolean\n      description: Install Reference data\n      default: true\n    refdata_dir:\n      type: string\n      description: path to store galaxy reference data\n      default: /cvmfs\n    refdata_repository_name:\n      type: string\n      description: Onedata space name, CernVM-FS repository name or subdirectory downaload name\n      default: \'elixir-italy.galaxy.refdata\'\n    refdata_provider_type:\n      type: string\n      description: Select Reference data provider type (Onedata, CernVM-FS or download)\n      default: \'cvmfs_preconfigured\'\n    refdata_provider:\n      type: string\n      description: Oneprovider for reference data\n      default: \'not_a_provider\'\n    refdata_token:\n      type: string\n      description: Access token for reference data\n      default: \'not_a_token\'\n    refdata_cvmfs_server_url:\n      type: string\n      description: CernVM-FS server, replica or stratum-zero\n      default: \'90.147.102.186\'\n    refdata_cvmfs_repository_name:\n      type: string\n      description: Reference data CernVM-FS repository name\n      default: \'elixir-italy.galaxy.refdata\'\n    refdata_cvmfs_key_file:\n      type: string\n      description: CernVM-FS public key\n      default: \'elixir-italy.galaxy.refdata.pub\'\n    refdata_cvmfs_proxy_url:\n      type: string\n      description: CernVM-FS proxy url\n      default: \'DIRECT\'\n    refdata_cvmfs_proxy_port:\n      type: integer\n      description: CernVM-FS proxy port\n      default: 80\n\n \n  node_templates:\n\n    galaxy:\n      type: tosca.nodes.indigo.GalaxyPortalAndStorage\n      properties:\n        storage_encryption: { get_input: storage_encryption }\n        vault_url: { get_input: vault_url }\n        vault_wrapping_token: { get_input: vault_wrapping_token }\n        vault_secret_path: { get_input: vault_secret_path }\n        vault_secret_key: { get_input: vault_secret_key }\n        admin_email: { get_input: admin_email }\n        admin_api_key: { get_input: admin_api_key }\n        version: { get_input: version }\n        instance_description: { get_input: in